changeSet.sql:
CREATE TABLE IF NOT EXISTS t_expenses
(
    expenses_id INT(5),
    pay_date    VARCHAR(20),
    receiver    INT(5),
    value       DEC
);

CREATE TABLE IF NOT EXISTS t_receivers
(
    receiver_no INT(5),
    name        VARCHAR(255)
);

COMMIT;

ALTER TABLE t_expenses ADD PRIMARY KEY (expenses_id), MODIFY expenses_id INT(5) NOT NULL AUTO_INCREMENT;
ALTER TABLE t_receivers ADD PRIMARY KEY (receiver_no), MODIFY receiver_no INT(5) NOT NULL AUTO_INCREMENT;
COMMIT;
